import styled from 'styled-components';

export const FriendListItem = styled.ul`
  display: flex;
  flex-direction: column;
  gap: 20px;
  background-color: #d9d9d9;
  box-shadow: 10px 11px 29px 0px rgba(0, 0, 0, 0.75);
  padding: 10px 15px;
`;
