import styled from 'styled-components';

export const Section = styled.section`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  background-color: #BEF3FA;
  
`;
export const Heading2 = styled.h2`
  
  color: white;

  width: 100%;
  text-align: center;
  
`;
